import React, { useState, useEffect } from 'react';
import { Button, TextField, Typography, Box } from '@mui/material';

const TablaProveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [nuevoProveedor, setNuevoProveedor] = useState({
    nombre: '',
    contacto: '',
    telefono: '',
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:5000/proveedores', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => res.json())
      .then(data => setProveedores(data))
      .catch(err => console.error(err));
  }, [token]);

  const handleChange = (e) => {
    setNuevoProveedor({
      ...nuevoProveedor,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregarProveedor = async () => {
    try {
      const response = await fetch('http://localhost:5000/proveedores', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(nuevoProveedor),
      });

      if (response.ok) {
        const nuevo = await response.json();
        setProveedores([...proveedores, nuevo]);
        setMostrarFormulario(false);
        setNuevoProveedor({ nombre: '', contacto: '', telefono: '' });
      } else {
        console.error('Error al agregar proveedor');
      }
    } catch (error) {
      console.error('Error en la petición:', error);
    }
  };

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>Lista de Proveedores</Typography>
      <Button variant="contained" onClick={() => setMostrarFormulario(!mostrarFormulario)}>
        {mostrarFormulario ? 'Cancelar' : 'Agregar Proveedor'}
      </Button>

      {mostrarFormulario && (
        <Box mt={3} mb={3}>
          <TextField
            label="Nombre"
            name="nombre"
            value={nuevoProveedor.nombre}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Contacto"
            name="contacto"
            value={nuevoProveedor.contacto}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Dirección"
            name="direccion"
            value={nuevoProveedor.telefono}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <Button variant="contained" color="primary" onClick={handleAgregarProveedor}>
            Guardar
          </Button>
        </Box>
      )}

      {/* Aquí puedes renderizar la tabla */}
      <Box>
      <Button variant="contained" color="primary" onClick={agregarProveedor}>
        Agregar Proveedor
      </Button>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Nombre</TableCell>
            <TableCell>Email</TableCell>
            <TableCell>Telefono</TableCell>
            <TableCell>Acciones</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {proveedores.map((proveedor) => (
            <TableRow key={proveedor.id}>
              <TableCell>{proveedor.nombre}</TableCell>
              <TableCell>{proveedor.contacto}</TableCell>
              <TableCell>{proveedor.telefono}</TableCell>
              <TableCell>
                <Button onClick={() => editarProveedor(proveedor.id)}>✏️</Button>
                <Button color="error" onClick={() => eliminarProveedor(proveedor.id)}>
                  ❌
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      </Box>
    </Box>
  );
};

export default TablaProveedores;
