import React, { useEffect, useRef, useState } from 'react';

const MostrarTurno = () => {
  const [turno, setTurno] = useState(null);
  const audioRef = useRef(null);
  const ultimoIdRef = useRef(null);
  const [animar, setAnimar] = useState(false);

  const fetchTurno = async () => {
    try {
        const res = await fetch("http://localhost:5000/api/turnos/ultimo");
        if (!res.ok) {
            console.error('Error en la respuesta del backend')
        }
        const data = await res.json();

        if (data.estado === 'atendido' && data.id !== ultimoIdRef.current) {
        ultimoIdRef.current = data.id;
        if (audioRef.current) {
            audioRef.current.play();
        }
        setAnimar(true);
        setTimeout(() => setAnimar(false), 1500);
        setTurno(data)
        }
    }catch (error) {
        console.error("Error al obtener turno:", error)
    }

  };

  useEffect(() => {
    fetchTurno();
    const intervalo = setInterval(fetchTurno, 3000);
    return () => clearInterval(intervalo);
  }, []);

  return (
    <div
      className={`turno-display ${animar ? 'animar' : ''}`}
    >
      <audio ref={audioRef} src="/campana.wav" />
      {turno ? (
        <>
          <div>Turno: <strong>{turno.numero}</strong></div>
          <div>MÃ³dulo: <strong>{turno.modulo}</strong></div>
        </>
      ) : (
        <p>Cargando turno...</p>
      )}
    </div>
  );
};

export default MostrarTurno;
