import React, { useState, useEffect } from 'react';
import { Box, Typography, Avatar, List, ListItem, ListItemButton, ListItemText } from '@mui/material';
import TablaProveedores from '../componentes/TablaProveedores';
import TablaEmpleados from '../componentes/TablaEmpleados';
import BarraDeNavegacion from '../componentes/BarraDeNavegacion';
import TablaProductos from '../componentes/TablaProductos';
import TablaVentas from '../componentes/TablaVentas';
import axios from 'axios';

const VistaAdministrador = () => {
  const [vistaActual, setVistaActual] = useState('proveedores');
  const [usuario, setUsuario] = useState({ usuario: ''});

  const menuOpciones = [
    { nombre: 'Proveedores', vista: 'proveedores' },
    { nombre: 'Empleados', vista: 'empleados' },
    { nombre: 'Productos', vista: 'productos' },
    { nombre: 'Ventas', vista: 'ventas' },
  ];

  useEffect(() => {
    axios.get('/usuario_actual', res.d)
      .then(res => {
        setUsuario({
          usuario: res.data.usuario
        });
      })
      .catch(err => {
        console.error('Error al obtener el usuario:', err);
      });
  }, []);

  return (
    <>
      <BarraDeNavegacion titulo="INVENTECH" />
      <Box sx={{ display: 'flex', height: '100vh', backgroundColor: '#FFFFF' }}>
        <Box
          sx={{
            width: 250,
            padding: 2,
            backgroundColor: '#1ba1e2',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar
            sx={{ width: 100, height: 100, marginBottom: 2 }}
            src="/ruta-a-la-foto"
          />
          <Typography 
            variant="h5" 
            sx={{ color: '#1a237e', fontWeight: 700 }}
          >
            {usuario.usuario || 'Cargando...'}
          </Typography>
          <List>
            {menuOpciones.map((opcion) => (
              <ListItem key={opcion.vista} disablePadding>
                <ListItemButton onClick={() => setVistaActual(opcion.vista)}>
                  <ListItemText primary={opcion.nombre} />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Box>
        <Box sx={{ flex: 1, padding: 3 }}>
          {vistaActual === 'proveedores' && <TablaProveedores />}
          {vistaActual === 'empleados' && <TablaEmpleados />}
          {vistaActual === 'productos' && <TablaProductos />}
          {vistaActual === 'ventas' && <TablaVentas />}
        </Box>
      </Box>
    </>
  );
};

export default VistaAdministrador;
