import React, { useState } from 'react';
import { TextField, Button, Typography, MenuItem, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const FormularioLogin = () => {
  const [usuario, setUsuario] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const manejarLogin = async () => {
    try {
      const respuesta = await fetch('http://localhost:5000/usuarios/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ usuario, contrasena })
      });

      const data = await respuesta.json();

      if (respuesta.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('rol', data.rol); // opcional
        localStorage.setItem('usuario_id', data.usuario_id); // opcional

        if (data.rol === 'administrador') {
          navigate('/administrador');
        } else if (data.rol === 'empleado') {
          navigate('/empleado');
        } else {
          setError('Rol no reconocido');
        }
      } else {
        setError(data.error || 'Error al iniciar sesión');
      }
    } catch (e) {
      setError('Error de red al intentar iniciar sesión');
    }
  };

  return (
    <Box
      sx={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',  
        alignItems: 'center',        
        backgroundColor: '#f5f5f5',  
      }}
    >
    <Box  
      sx={{
        maxWidth: 600,
        margin: 'auto',
        padding: 7,
        textAlign: 'center',
        backgroundColor: '#1ba1e2',
        borderRadius: 2,
      }}
    >
      <Typography variant="h5" sx={{ marginBottom: 2 }}>
        BIENVENIDO A INVENTECH
      </Typography>
      <TextField
        label="Usuario"
        variant="outlined"
        fullWidth
        margin="normal"
        value={usuario}
        onChange={(e) => setUsuario(e.target.value)}
      />
      <TextField
        label="Contraseña"
        variant="outlined"
        type="password"
        fullWidth
        margin="normal"
        value={contraseña}
        onChange={(e) => setContraseña(e.target.value)}
      />

      <Button
        variant="contained"
        color="primary"
        fullWidth
        sx={{ marginTop: 2 }}
        onClick={manejarLogin}
      >
        Iniciar Sesión
      </Button>
    </Box>
    </Box>
  );
};

export default FormularioLogin;
