import React, { useEffect, useState } from 'react';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
  Button, IconButton, TextField, Select, MenuItem, InputLabel, FormControl
} from '@mui/material';
import { Delete } from '@mui/icons-material';

const TablaVentas = () => {
  const [ventas, setVentas] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const [productos, setProductos] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [formulario, setFormulario] = useState({
    producto_id: '',
    empleado_id: '',
    cantidad_vendida: ''
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    obtenerVentas();
    obtenerProductos();
    obtenerEmpleados();
  }, []);

  const obtenerVentas = async () => {
    try {
      const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/ventas', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setVentas(data);
    } catch (error) {
      console.error('Error al obtener ventas:', error);
    }
  };

  const obtenerProductos = async () => {
    const res = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/productos', {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setProductos(data);
  };

  const obtenerEmpleados = async () => {
    const res = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/empleados', {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setEmpleados(data);
  };

  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregar = async () => {
    try {
      const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/ventas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const nuevaVenta = await response.json();
        setVentas([...ventas, nuevaVenta]);
        setFormulario({ producto_id: '', empleado_id: '', cantidad_vendida: '' });
        setMostrarFormulario(false);
        setMensaje('Venta agregado correctamente');
        setTimeout(() => setMensaje(''), 5000);
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Error al agregar la venta');
      }
    } catch (error) {
      console.error('Error al agregar venta:', error);
    }
  };

  const handleEliminar = async (id) => {
    const confirm = window.confirm('¿Seguro que deseas eliminar esta venta?');
    if (!confirm) return;

    try {
      const response = await fetch(`https://4gmd939z-5000.use2.devtunnels.ms/ventas/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        setVentas(ventas.filter((v) => v.id !== id));
      }
    } catch (error) {
      console.error('Error al eliminar esta venta:', error);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Ventas</h2>
      {mensaje && (
        <div style={{
          marginBottom: '1rem',
          padding: '0.5rem 1rem',
          backgroundColor: '#d4edda',
          color: '#155724',
          borderRadius: '5px',
          fontWeight: 'bold'
        }}>
          {mensaje}
        </div>
      )}
      <Button
        variant="contained"
        color={mostrarFormulario ? 'secondary' : 'primary'}
        onClick={() => {
          setMostrarFormulario(!mostrarFormulario);
          setFormulario({ producto_id: '', empleado_id: '', cantidad_vendida: '' });
        }}
        style={{ marginBottom: '1rem' }}
      >
        {mostrarFormulario ? 'Cancelar' : 'Agregar venta'}
      </Button>

      {mostrarFormulario && (
        <div style={{ marginBottom: '2rem' }}>
          <FormControl style={{ marginRight: '1rem', minWidth: 150 }}>
            <InputLabel style={{ color: '#555555' }}>Producto</InputLabel>
            <Select
              name="producto_id"
              value={formulario.producto_id}
              onChange={handleChange}
            >
              {productos.map((p) => (
                <MenuItem key={p.id} value={p.id}>{p.nombre}</MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl style={{ marginRight: '1rem', minWidth: 150 }}>
            <InputLabel style={{ color: '#555555' }}>Empleado</InputLabel>
            <Select
              name="empleado_id"
              value={formulario.empleado_id}
              onChange={handleChange}
            >
              {empleados.map((e) => (
                <MenuItem key={e.id} value={e.id}>{e.nombre}</MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            name="cantidad_vendida"
            label="Cantidad"
            type="number"
            value={formulario.cantidad_vendida}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />

          <Button
            variant="contained"
            color="success"
            onClick={handleAgregar}
          >
            Guardar
          </Button>
        </div>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead style={{ backgroundColor: '#1976d2' }}>
            <TableRow>
              <TableCell style={{ color: 'white' }}>ID</TableCell>
              <TableCell style={{ color: 'white' }}>Producto</TableCell>
              <TableCell style={{ color: 'white' }}>Empleado</TableCell>
              <TableCell style={{ color: 'white' }}>Cantidad</TableCell>
              <TableCell style={{ color: 'white' }}>Fecha</TableCell>
              <TableCell style={{ color: 'white' }}>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {ventas.map((venta) => (
              <TableRow key={venta.id}>
                <TableCell>{venta.id}</TableCell>
                <TableCell>{venta.producto?.nombre || venta.producto_id}</TableCell>
                <TableCell>{venta.empleado?.nombre || venta.empleado_id}</TableCell>
                <TableCell>{venta.cantidad_vendida}</TableCell>
                <TableCell>{new Date(venta.fecha).toLocaleString()}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleEliminar(venta.id)} color="error">
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {ventas.length === 0 && (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  No hay ventas registradas.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TablaVentas;
