import React, { useState } from 'react';

const PedirTurno = () => {
  const [turno, setTurno] = useState(null);

  const pedirTurno = async () => {
    const res = await fetch("http://localhost:5000/api/turnos/", {
      method: 'POST'
    });
    const data = await res.json();
    setTurno(data);
  };

  return (
    <div className="container card">
      <h2>Pedir Turno</h2>
      <button onClick={pedirTurno}>Solicitar Turno</button>
      {turno && (
        <p style={{ fontSize: '2rem', marginTop: '1rem' }}>
            Turno: <strong>{turno.numero}</strong><br />
            Por favor espere a ser llamado en pantalla...
        </p>
      )}

    </div>
  );
};

export default PedirTurno;
