import React, { useState } from 'react';

const PanelCajero = () => {
  const [turno, setTurno] = useState(null);
  const [modulo, setModulo] = useState(1); // Puedes hacer que el cajero seleccione su m贸dulo

  const llamarSiguiente = async () => {
    const res = await fetch("http://localhost:5000/api/turnos/siguiente", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ modulo })
    });

    const data = await res.json();
    setTurno(data);
  };

  return (
    <div className="container card">
      <h2>Panel del Cajero</h2>

      <label>
        M贸dulo:
        <input
          type="number"
          min="1"
          max="10"
          value={modulo}
          onChange={(e) => setModulo(Number(e.target.value))}
        />
      </label>

      <br /><br />

      <button onClick={llamarSiguiente}>Llamar Siguiente Turno</button>

      {turno && turno.numero ? (
        <p style={{ fontSize: '2rem', marginTop: '1rem' }}>
          Llamando al Turno: <strong>{turno.numero}</strong> (M贸dulo {turno.modulo})
        </p>
      ) : turno?.mensaje ? (
        <p> style={{ marginTop: '1rem' }}No hay turnos disponibles.</p>
      ) : null}
    </div>
  );
};

export default PanelCajero;
