import React, { useEffect, useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, IconButton, TextField } from '@mui/material';
import { Delete, Edit } from '@mui/icons-material';

const TablaProductos = () => {
  const [productos, setProductos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [modoEdicion, setModoEdicion] = useState(false);
  const [productoActual, setProductoActual] = useState(null);
  const [formulario, setFormulario] = useState({ nombre: '', descripcion: '', precio: '', cantidad: '' });

  const token = localStorage.getItem('token');

  useEffect(() => {
    obtenerProductos();
  }, []);

  const obtenerProductos = async () => {
    try {
      const response = await fetch('http://localhost:5000/productos', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setProductos(data);
    } catch (error) {
      console.error('Error al obtener productos:', error);
    }
  };

  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregar = async () => {
    try {
      const response = await fetch('http://localhost:5000/productos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const nuevo = await response.json();
        setProductos([...productos, nuevo]);
        setFormulario({ nombre: '', descripcion: '', precio: '', cantidad: '' });
        setMostrarFormulario(false);
      }
    } catch (error) {
      console.error('Error al agregar producto:', error);
    }
  };

  const handleEditar = (producto) => {
    setFormulario(producto);
    setProductoActual(producto);
    setModoEdicion(true);
    setMostrarFormulario(true);
  };

  const handleGuardarEdicion = async () => {
    try {
      const response = await fetch(`http://localhost:5000/productos/${productoActual.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const actualizado = await response.json();
        setProveedores(
          proveedores.map((p) => (p.id === proveedorActual.id ? actualizado : p))
        );
        setFormulario({ nombre: '', email: '', telefono: '' });
        setProveedorActual(null);
        setModoEdicion(false);
        setMostrarFormulario(false);
      }
    } catch (error) {
      console.error('Error al editar proveedor:', error);
    }
  };

  const handleEliminar = async (id) => {
    try {
      const confirm = window.confirm('¿Seguro que deseas eliminar este proveedor?');
      if (!confirm) return;

      const response = await fetch(`http://localhost:5000/proveedores/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setProveedores(proveedores.filter((p) => p.id !== id));
      }
    } catch (error) {
      console.error('Error al eliminar proveedor:', error);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Proveedores</h2>

      <Button
        variant="contained"
        color={mostrarFormulario ? 'secondary' : 'primary'}
        onClick={() => {
          setMostrarFormulario(!mostrarFormulario);
          setFormulario({ nombre: '', email: '', telefono: '' });
          setModoEdicion(false);
        }}
        style={{ marginBottom: '1rem' }}
      >
        {mostrarFormulario ? 'Cancelar' : 'Agregar proveedor'}
      </Button>

      {mostrarFormulario && (
        <div style={{ marginBottom: '2rem' }}>
          <TextField
            name="nombre"
            label="Nombre"
            value={formulario.nombre}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
          />
          <TextField
            name="email"
            label="Email"
            value={formulario.email}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
          />
          <TextField
            name="telefono"
            label="Telefono"
            value={formulario.telefono}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
          />
          <Button
            variant="contained"
            color="success"
            onClick={modoEdicion ? handleGuardarEdicion : handleAgregar}
          >
            {modoEdicion ? 'Guardar cambios' : 'Guardar'}
          </Button>
        </div>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead style={{ backgroundColor: '#1976d2' }}>
            <TableRow>
              <TableCell style={{ color: 'white' }}>Nombre</TableCell>
              <TableCell style={{ color: 'white' }}>Email</TableCell>
              <TableCell style={{ color: 'white' }}>Telefono</TableCell>
              <TableCell style={{ color: 'white' }}>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {proveedores.map((proveedor) => (
              <TableRow key={proveedor.id}>
                <TableCell>{proveedor.nombre}</TableCell>
                <TableCell>{proveedor.email}</TableCell>
                <TableCell>{proveedor.telefono}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleEditar(proveedor)} color="primary">
                    <Edit />
                  </IconButton>
                  <IconButton onClick={() => handleEliminar(proveedor.id)} color="error">
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {proveedores.length === 0 && (
              <TableRow>
                <TableCell colSpan={4} align="center">
                  No hay proveedores registrados.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TablaProveedores;
