import React, { useEffect, useRef, useState } from 'react';

const MostrarTurno = () => {
  const [turno, setTurno] = useState(null);
  const audioRef = useRef(null);
  const ultimoIdRef = useRef(null);
  const [animar, setAnimar] = useState(false);

  const fetchTurno = async () => {
    const res = await fetch("http://localhost:5000/api/turnos/ultimo");
    const data = await res.json();

    // Reproducir sonido solo si hay un nuevo turno
    if (data.id && data.id !== ultimoIdRef.current) {
      ultimoIdRef.current = data.id;
      if (audioRef.current) {
        audioRef.current.play();
      }
      setAnimar(true);
      setTimeout(() => setAnimar(false), 1500);
    }

    setTurno(data);
  };

  useEffect(() => {
    fetchTurno();
    const intervalo = setInterval(fetchTurno, 3000);
    return () => clearInterval(intervalo);
  }, []);

  return (
    <div style={{
      backgroundColor: '#000',
      color: '#0f0',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '6em',
      transition: 'transform 0.5s ease-in-out',
      transform: animar ? 'scale(1.1)' : 'scale(1)'
    }}>
      <audio ref={audioRef} src="/campana.wav" />
      {turno ? (
        <>
          <div>Turno: <strong>{turno.numero}</strong></div>
          <div>MÃ³dulo: <strong>{turno.modulo}</strong></div>
        </>
      ) : (
        <p>Cargando turno...</p>
      )}
    </div>
  );
};

export default MostrarTurno;
