import React, { useEffect, useRef, useState } from 'react';

const MostrarTurno = () => {
  const [turno, setTurno] = useState(null);
  const [iniciado, setIniciado] = useState(false);
  const [animar, setAnimar] = useState(false);
  const ultimoIdRef = useRef(null);
  const audioRef = useRef(null);

  const fetchTurno = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/turnos/ultimo");
      const data = await res.json();

      if (data.id && data.id !== ultimoIdRef.current) {
        ultimoIdRef.current = data.id;

        if (audioRef.current) {
          audioRef.current.play().catch((e) => console.warn("Audio bloqueado"));
        }

        setAnimar(true);
        setTimeout(() => setAnimar(false), 1500);
      }

      setTurno(data);
    } catch (error) {
      console.error("Error al obtener turno:", error);
    }
  };

  useEffect(() => {
    if (iniciado) {
      fetchTurno();
      const intervalo = setInterval(fetchTurno, 3000);
      return () => clearInterval(intervalo);
    }
  }, [iniciado]);

  if (!iniciado) {
    return (
      <div style={{
        height: '100vh',
        backgroundColor: '#000',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column'
      }}>
        <h1>Bienvenido</h1>
        <button
          onClick={() => setIniciado(true)}
          style={{
            padding: '20px 40px',
            fontSize: '1.5rem',
            backgroundColor: 'red',
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            cursor: 'pointer'
          }}
        >
          Activar pantalla
        </button>
      </div>
    );
  }

  return (
    <div className={`turno-display ${animar ? 'animar' : ''}`} style={{
      backgroundColor: '#000',
      color: '#fff',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '5rem',
      transition: 'transform 0.5s ease-in-out',
      transform: animar ? 'scale(1.1)' : 'scale(1)',
    }}>
      <audio ref={audioRef} src="/campana.wav" />
      {turno && turno.numero ? (
        <>
          <div style={{ color: '#f00' }}>Turno: <strong>{turno.numero}</strong></div>
          {turno.modulo && (
            <div>MÃ³dulo: <strong>{turno.modulo}</strong></div>
          )}
        </>
      ) : (
        <p style={{ fontSize: '2rem' }}>Esperando turno...</p>
      )}
    </div>
  );
};

export default MostrarTurno;
