import React, { useEffect, useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, IconButton, TextField } from '@mui/material';
import { Delete, Edit } from '@mui/icons-material';

const TablaEmpleados = () => {
  const [empleados, setEmpleados] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [modoEdicion, setModoEdicion] = useState(false);
  const [empleadoActual, setEmpleadoActual] = useState(null);
  const [formulario, setFormulario] = useState({ nombre: '', tipo_documento: '', numero_documento: '', email: '', direccion: '', telefono: '', cargo: '', usuario: '', contrasena:'', rol:'', usuario_id: '' });

  const token = localStorage.getItem('token');

  useEffect(() => {
    obtenerEmpleados();
  }, []);

  const obtenerEmpleados = async () => {
    try {
      const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/empleados', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setEmpleados(data);
    } catch (error) {
      console.error('Error al obtener empleados:', error);
    }
  };

  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregar = async () => {
  try {
    const formData = new FormData();
    for (const key in formulario) {
      formData.append(key, formulario[key]);
    }

    const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/empleados', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    const data = await response.json();
    console.log('Respuesta del servidor:', data);
    obtenerEmpleados();
    setFormulario({ nombre: '', tipo_documento: '', numero_documento: '', email: '', direccion: '', telefono: '', cargo: '', usuario: '', contrasena:'', rol:'', usuario_id: '' });
    setMostrarFormulario(false);
    setMensaje('Producto agregado correctamente');
        setTimeout(() => setMensaje(''), 5000);
  } catch (error) {
    console.error('Error al agregar empleado:', error);
  }
};


  const handleEditar = (empleado) => {
    setFormulario(empleado);
    setEmpleadoActual(empleado);
    setModoEdicion(true);
    setMostrarFormulario(true);
  };

  const handleGuardarEdicion = async () => {
    try {
      const response = await fetch(`https://4gmd939z-5000.use2.devtunnels.ms/empleados/${empleadoActual.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const actualizado = await response.json();
        setEmpleados(
          empleados.map((p) => (p.id === empleadoActual.id ? actualizado : p))
        );
        setFormulario({ nombre: '', tipo_documento: '', numero_documento: '', email: '', direccion: '', telefono: '', cargo: '', usuario: '', contrasena:'', rol:'', usuario_id: '' });
        setEmpleadoActual(null);
        setModoEdicion(false);
        setMostrarFormulario(false);
      }
    } catch (error) {
      console.error('Error al editar empleado:', error);
    }
  };

  const handleEliminar = async (id) => {
    try {
      const confirm = window.confirm('¿Seguro que deseas eliminar este empleado?');
      if (!confirm) return;

      const response = await fetch(`https://4gmd939z-5000.use2.devtunnels.ms/empleados/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setEmpleados(empleados.filter((p) => p.id !== id));
      }
    } catch (error) {
      console.error('Error al eliminar empleado:', error);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Empleados</h2>
      {mensaje && (
        <div style={{
          marginBottom: '1rem',
          padding: '0.5rem 1rem',
          backgroundColor: '#d4edda',
          color: '#155724',
          borderRadius: '5px',
          fontWeight: 'bold'
        }}>
          {mensaje}
        </div>
      )}
      <Button
        variant="contained"
        color={mostrarFormulario ? 'secondary' : 'primary'}
        onClick={() => {
          setMostrarFormulario(!mostrarFormulario);
          setFormulario({ nombre: '', tipo_documento: '', numero_documento: '', email: '', direccion: '', telefono: '', cargo: '', usuario: '', contrasena:'', rol:'', usuario_id: '' });
          setModoEdicion(false);
        }}
        style={{ marginBottom: '1rem' }}
      >
        {mostrarFormulario ? 'Cancelar' : 'Agregar empleado'}
      </Button>

      {mostrarFormulario && (
        <div style={{ marginBottom: '2rem', color: '#555555' }}>
          <TextField
            name="nombre"
            label="Nombre"
            value={formulario.nombre}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="tipo_documento"
            label="Tipo Documento"
            value={formulario.tipo_documento}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="numero_documento"
            label="Número Documento"
            value={formulario.numero_documento}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="email"
            label="Email"
            value={formulario.email}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="direccion"
            label="Direccion"
            value={formulario.direccion}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="telefono"
            label="Telefono"
            value={formulario.telefono}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="cargo"
            label="Cargo"
            value={formulario.cargo}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="usuario"
            label="Usuario"
            value={formulario.usuario}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="contrasena"
            label="Contraseña"
            value={formulario.contrasena}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="rol"
            label="Rol"
            value={formulario.rol}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="usuario_id"
            label="Usuario"
            value={formulario.usuario_id}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <Button
            variant="contained"
            color="success"
            onClick={modoEdicion ? handleGuardarEdicion : handleAgregar}
          >
            {modoEdicion ? 'Guardar cambios' : 'Guardar'}
          </Button>
        </div>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead style={{ backgroundColor: '#1976d2' }}>
            <TableRow>
              <TableCell style={{ color: 'white' }}>Nombre</TableCell>
              <TableCell style={{ color: 'white' }}>Tipo de documento</TableCell>
              <TableCell style={{ color: 'white' }}>Numero de documento</TableCell>
              <TableCell style={{ color: 'white' }}>Email</TableCell>
              <TableCell style={{ color: 'white' }}>Direccion</TableCell>
              <TableCell style={{ color: 'white' }}>Telefono</TableCell>
              <TableCell style={{ color: 'white' }}>Cargo</TableCell>
              <TableCell style={{ color: 'white' }}>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {empleados.map((empleado) => (
              <TableRow key={empleado.id}>
                <TableCell>{empleado.nombre}</TableCell>
                <TableCell>{empleado.tipo_documento}</TableCell>
                <TableCell>{empleado.numero_documento}</TableCell>
                <TableCell>{empleado.email}</TableCell>
                <TableCell>{empleado.direccion}</TableCell>
                <TableCell>{empleado.telefono}</TableCell>
                <TableCell>{empleado.cargo}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleEditar(empleado)} color="primary">
                    <Edit />
                  </IconButton>
                  <IconButton onClick={() => handleEliminar(empleado.id)} color="error">
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {empleados.length === 0 && (
              <TableRow>
                <TableCell colSpan={4} align="center">
                  No hay empleados registrados.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TablaEmpleados;
