import React, { useEffect, useState } from 'react';

const MostrarTurno = () => {
    const [turno, setTurno] = useState(null);

    const fetchTurno = async () => {
        const res = await fetch("http://localhost:5000/api/turnos/ultimo");
        const data = await res.json();
        setTurno(data);
    };

    useEffect(() => {
        fetchTurno();
        const intervalo = setInterval(fetchTurno, 3000); // cada 3 segundos
        return () => clearInterval(intervalo);
    }, []);

    return (
        <div style={{ fontSize: '3em', textAlign: 'center', marginTop: '50px' }}>
            {turno ? (
                <div>
                    <p>Turno: <strong>{turno.numero}</strong></p>
                    <p>MÃ³dulo: <strong>{turno.modulo}</strong></p>
                </div>
            ) : (
                <p>Cargando turno...</p>
            )}
        </div>
    );
};

export default MostrarTurno;
