import React, { useEffect, useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, IconButton, TextField } from '@mui/material';
import { Delete, Edit } from '@mui/icons-material';

const TablaProductos = () => {
  const [productos, setProductos] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [modoEdicion, setModoEdicion] = useState(false);
  const [productoActual, setProductoActual] = useState(null);
  const [formulario, setFormulario] = useState({ nombre: '', descripcion: '', precio: '', cantidad: '' });

  const token = localStorage.getItem('token');

  useEffect(() => {
    obtenerProductos();
  }, []);

  const obtenerProductos = async () => {
    try {
      const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/productos', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setProductos(data);
    } catch (error) {
      console.error('Error al obtener productos:', error);
    }
  };

  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregar = async () => {
    try {
      const response = await fetch('https://4gmd939z-5000.use2.devtunnels.ms/productos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const nuevo = await response.json();
        setProductos([...productos, nuevo]);
        setFormulario({ nombre: '', descripcion: '', precio: '', cantidad: '' });
        setMostrarFormulario(false);
        setMensaje('Producto agregado correctamente');
        setTimeout(() => setMensaje(''), 5000);
      }
    } catch (error) {
      console.error('Error al agregar producto:', error);
    }
  };

  const handleEditar = (producto) => {
    setFormulario(producto);
    setProductoActual(producto);
    setModoEdicion(true);
    setMostrarFormulario(true);
  };

  const handleGuardarEdicion = async () => {
    try {
      const response = await fetch(`https://4gmd939z-5000.use2.devtunnels.ms/productos/${productoActual.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formulario),
      });

      if (response.ok) {
        const actualizado = await response.json();
        setProductos(
          productos.map((p) => (p.id === productoActual.id ? actualizado : p))
        );
        setFormulario({ nombre: '', descripcion: '', precio: '', cantidad: '' });
        setProductoActual(null);
        setModoEdicion(false);
        setMostrarFormulario(false);
        setMensaje('Producto actualizado correctamente');
        setTimeout(() => setMensaje(''), 5000);
      }
    } catch (error) {
      console.error('Error al editar producto:', error);
    }
  };

  const handleEliminar = async (id) => {
    try {
      const confirm = window.confirm('¿Seguro que deseas eliminar este producto?');
      if (!confirm) return;

      const response = await fetch(`https://4gmd939z-5000.use2.devtunnels.ms/productos/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setProductos(productos.filter((p) => p.id !== id));
        setMensaje('Producto eliminado correctamente');
        setTimeout(() => setMensaje(''), 5000);
      }
    } catch (error) {
      console.error('Error al eliminar producto:', error);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Productos</h2>
      
      <Button
        variant="contained"
        color={mostrarFormulario ? 'secondary' : 'primary'}
        onClick={() => {
          setMostrarFormulario(!mostrarFormulario);
          setFormulario({ nombre: '', descripcion: '', precio: '', cantidad:'' });
          setModoEdicion(false);
        }}
        style={{ marginBottom: '1rem' }}
      >
        {mostrarFormulario ? 'Cancelar' : 'Agregar Producto'}
      </Button>

      {mostrarFormulario && (
        <div style={{ marginBottom: '2rem' }}>
          <TextField
            name="nombre"
            label="Nombre"
            value={formulario.nombre}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="descripcion"
            label="Descripcion"
            value={formulario.descripcion}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="precio"
            label="Precio"
            value={formulario.precio}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <TextField
            name="cantidad"
            label="Cantidad"
            value={formulario.cantidad}
            onChange={handleChange}
            style={{ marginRight: '1rem' }}
            InputLabelProps={{
            style: { color: '#555555' }
            }}
          />
          <Button
            variant="contained"
            color="success"
            onClick={modoEdicion ? handleGuardarEdicion : handleAgregar}
          >
            {modoEdicion ? 'Guardar cambios' : 'Guardar'}
          </Button>
        </div>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead style={{ backgroundColor: '#1976d2' }}>
            <TableRow>
              <TableCell style={{ color: 'white' }}>ID</TableCell>
              <TableCell style={{ color: 'white' }}>Nombre</TableCell>
              <TableCell style={{ color: 'white' }}>Descripcion</TableCell>
              <TableCell style={{ color: 'white' }}>Precio</TableCell>
              <TableCell style={{ color: 'white' }}>Cantidad</TableCell>
              <TableCell style={{ color: 'white' }}>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {productos.map((producto) => (
              <TableRow key={producto.id}>
                <TableCell>{producto.id}</TableCell>
                <TableCell>{producto.nombre}</TableCell>
                <TableCell>{producto.descripcion}</TableCell>
                <TableCell>{producto.precio}</TableCell>
                <TableCell>{producto.cantidad}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleEditar(producto)} color="primary">
                    <Edit />
                  </IconButton>
                  <IconButton onClick={() => handleEliminar(producto.id)} color="error">
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {productos.length === 0 && (
              <TableRow>
                <TableCell colSpan={4} align="center">
                  No hay productos registrados.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TablaProductos;
