import React, { useEffect, useState } from 'react';

const MostrarTurno = () => {
    const [turno, setTurno] = useState(null);

    const fetchTurno = async () => {
        const res = await fetch("http://localhost:5000/api/turnos/ultimo");
        const data = await res.json();
        setTurno(data);
    };

    useEffect(() => {
        fetchTurno();
        const intervalo = setInterval(fetchTurno, 3000); // cada 3 segundos
        return () => clearInterval(intervalo);
    }, []);

    return (
<div style={{
  fontSize: '6em',
  textAlign: 'center',
  marginTop: '15%',
  backgroundColor: '#000',
  color: '#0f0',
  height: '100vh'
}}>
