import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableHead, TableRow, Box } from '@mui/material';

const TablaProveedores = () => {
  const [proveedores, setProveedores] = useState([]);

  // Obtener el token desde localStorage
  const token = localStorage.getItem('token');

  // Obtener la lista de proveedores (con token)
  useEffect(() => {
    fetch('http://localhost:5000/proveedores', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then((res) => {
        if (!res.ok) throw new Error("No autorizado");
        return res.json();
      })
      .then((data) => setProveedores(data))
      .catch((error) => {
        console.error('Error al obtener proveedores:', error);
      });
  }, [token]);

  const agregarProveedor = () => {
    // L贸gica para agregar proveedor
  };

  const editarProveedor = (id) => {
    // L贸gica para editar proveedor
  };

  const eliminarProveedor = (id) => {
    fetch(`http://localhost:5000/proveedores/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${token}`
      }
    }).then(() => {
      setProveedores(proveedores.filter((p) => p.id !== id));
    });
  };

  return (
    <Box>
      <Button variant="contained" color="primary" onClick={agregarProveedor}>
        Agregar Proveedor
      </Button>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Nombre</TableCell>
            <TableCell>Email</TableCell>
            <TableCell>Direccion</TableCell>
            <TableCell>Telefono</TableCell>
            <TableCell>Acciones</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {proveedores.map((proveedor) => (
            <TableRow key={proveedor.id}>
              <TableCell>{proveedor.nombre}</TableCell>
              <TableCell>{proveedor.contacto}</TableCell>
              <TableCell>{proveedor.telefono}</TableCell>
              <TableCell>{proveedor.direccion}</TableCell>
              <TableCell>
                <Button onClick={() => editarProveedor(proveedor.id)}>Editar</Button>
                <Button color="error" onClick={() => eliminarProveedor(proveedor.id)}>
                  Eliminar
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Box>
  );
};

export default TablaProveedores;
