import React, { useEffect, useRef, useState } from "react";

const MostrarTurno = () => {
  const [turnoActual, setTurnoActual] = useState(null);
  const [historial, setHistorial] = useState([]);
  const audioRef = useRef(null);
  const ultimoIdRef = useRef(null);
  const [mostrarAnimacion, setMostrarAnimacion] = useState(false);

  const fetchTurno = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/turnos/ultimo");
      const data = await res.json();

      if (data.id && data.id !== ultimoIdRef.current) {
        ultimoIdRef.current = data.id;

        if (audioRef.current) audioRef.current.play();
        setTurnoActual(data);
        setHistorial((prev) => [data, ...prev].slice(0, 5));
        setMostrarAnimacion(true);

        setTimeout(() => setMostrarAnimacion(false), 5000);
      }
    } catch (error) {
      console.error("Error obteniendo turno:", error);
    }
  };

  useEffect(() => {
    fetchTurno();
    const intervalo = setInterval(fetchTurno, 3000);
    return () => clearInterval(intervalo);
  }, []);

  const estilos = {
    pantalla: {
      height: "100vh",
      width: "100%",
      background: "black",
      color: "white",
      overflow: "hidden",
      position: "relative",
      fontFamily: "sans-serif",
    },
    animacionContainer: (show) => ({
      position: "absolute",
      width: "100%",
      height: "100%",
      transition: "opacity 1s ease, transform 1s ease",
      opacity: show ? 1 : 0,
      transform: show ? "translateX(0)" : "translateX(-100%)",
      zIndex: show ? 2 : 1,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      flexDirection: "column",
      fontSize: "5rem",
      color: "red",
    }),
    tablaContainer: (show) => ({
      position: "absolute",
      width: "100%",
      height: "100%",
      transition: "opacity 1s ease, transform 1s ease",
      opacity: show ? 0 : 1,
      transform: show ? "translateX(100%)" : "translateX(0)",
      zIndex: show ? 1 : 2,
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "2rem",
    }),
    tabla: {
      flex: 2,
    },
    tablaTitulo: {
      fontSize: "3rem",
      marginBottom: "1rem",
      color: "red",
    },
    tablaStyle: {
      width: "100%",
      borderCollapse: "collapse",
      fontSize: "2rem",
    },
    thtd: {
      padding: "0.5rem 1rem",
      borderBottom: "1px solid white",
      textAlign: "center",
    },
    logo: {
      flex: 1,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    logoImg: {
      maxWidth: "80%",
    },
  };

  return (
    <div style={estilos.pantalla}>
      <audio ref={audioRef} src="/campana.wav" />

      {/* Animación turno grande */}
      <div style={estilos.animacionContainer(mostrarAnimacion)}>
        {turnoActual && (
          <>
            <div>
              Turno <span style={{ color: "white" }}>{turnoActual.numero}</span>
            </div>
            <div>
              Módulo <span style={{ color: "white" }}>{turnoActual.modulo}</span>
            </div>
          </>
        )}
      </div>

      {/* Tabla historial */}
      <div style={estilos.tablaContainer(mostrarAnimacion)}>
        <div style={estilos.tabla}>
          <h2 style={estilos.tablaTitulo}>TURNOS</h2>
          <table style={estilos.tablaStyle}>
            <thead>
              <tr>
                <th style={estilos.thtd}>Turno</th>
                <th style={estilos.thtd}>Módulo</th>
              </tr>
            </thead>
            <tbody>
              {historial.map((t, idx) => (
                <tr key={idx}>
                  <td style={estilos.thtd}>{t.numero}</td>
                  <td style={estilos.thtd}>{t.modulo}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div style={estilos.logo}>
          <img
            src="/logo.png"
            alt="Logo Empresa"
            style={estilos.logoImg}
          />
        </div>
      </div>
    </div>
  );
};

export default MostrarTurno;
